let left_light_intensity = global.get("left_light_intensity");
let right_light_intensity = global.get("right_light_intensity");

if (left_light_intensity > right_light_intensity) {
    return [{ payload: "Left LDR" }, {payload: left_light_intensity}];
} else {
    return [{ payload: "Right LDR" }, {payload: right_light_intensity}];
}

let max_intensity_ldr = global.get("max_intensity_ldr");
let max_intensity = parseFloat(global.get("max_intensity"));
let minimum_angle = parseInt(global.get("minimum_angle"));
let controlling_factor = parseFloat(global.get("controlling_factor"));
let calc_motor_angle, motor_angle;
let d_value;

if (max_intensity_ldr == "Left LDR") {
    d_value = 1.5;
} else {
    d_value = 0.5;
}

calc_motor_angle = Math.round(minimum_angle * d_value + (180 - minimum_angle) * max_intensity * controlling_factor);
motor_angle = (calc_motor_angle < 180) ? calc_motor_angle : 180;

return { payload: motor_angle }

