const now = new Date();
const hours = now.getHours();
const minutes = now.getMinutes();
const seconds = now.getSeconds();
const currentTimeInSecs = (hours * 60 * 60) + (minutes * 60) + (seconds);
const alarms_on = global.get("alarms_on");
let alarm_time_count = global.get("alarm_time_count");

if (alarm_time_count === undefined) alarm_time_count = 0;

if (!alarms_on) {
    return [{ enabled: false }, { payload: "Alarms are Disabled" }, { alarm_time_count: alarm_time_count }]
}

if (msg.payload !== undefined && currentTimeInSecs - msg.payload.latestTime < 2) {
    global.set("alarm_time_count", alarm_time_count + 1);
    return [{ enabled: true }, {
        payload: "Alarm " + msg.payload.latestAlarm + " Ringing"
    }, { alarm_time_count: alarm_time_count }]
}
else {
    if (alarm_time_count > 0){
        global.set("alarm_time_count", 0);
    }
    return [{ enabled: false }, {
        payload: "No Alarms Ringing"
    }, { alarm_time_count: alarm_time_count }]
}

const now = new Date();
const hours = now.getHours();
const minutes = now.getMinutes();
const seconds = now.getSeconds();
const totalTimeInSecs = (hours * 60 * 60) + (minutes * 60) + (seconds);

let latestRinging = {
    latestAlarm: msg.payload,
    latestTime: totalTimeInSecs
};

return { payload: latestRinging };

const queried_array = msg.payload;
let medicines = [];

medicines.unshift({ "Custom": {"medicine_name": "Custom"}});
medicines.unshift({ "New Medicine": { "medicine_name": "New" }});

for (let index = 0; index < queried_array.length; index++) {
    const element = queried_array[index];
    const element_name = element.RowKey._;
    medicines.unshift({
        [element_name] : {
            "medicine_name": element.medicine_name._,
            "minimum_angle": element.minimum_angle._,
            "controlling_factor": element.controlling_factor._
        }
    });
}

return { medicines: medicines };

const status = msg.payload.status;
const medicine_details = msg.payload.details;

if (status) {
    const tableInsertion = {
        "tableName": "medicines",
        "action": "I",
        "partitionKey": "medicine",
        "rowKey": medicine_details.medicine_name,
